sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (!g_form.isNewRecord() && (isLoading || newValue === '' || newValue === oldValue)) {
      return;
   }

   //Type appropriate comment here, and begin script below
	var ajax = new GlideAjax('GetEmailAddress');
	ajax.addParam('sysparm_name', 'getEmail');
	ajax.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	ajax.getXML(function(response) {
		var email = response.responseXML.documentElement.getAttribute('answer');
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', email);
	});

}
