sysauto_script.script:
try {
	var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

	var toClose = new GlideRecord('x_58872_needit_needit_task');
	toClose.addQuery('due_date', '<=', gs.daysAgo(howLongToClose));
	toClose.addQuery('state', '<', 3);

	toClose.query();

	while(toClose.next()) {
		gs.info('Auto close NeedIt Task ' + toClose.number);
		toClose.work_notes = 'Record closed because it was too far past the due date.';
		toClose.state = 4;
		toClose.update();
	}
}
catch(e) {
	gs.info('Find and Close Long Overdue NeedIt Tasks failed with message: ' + e.message);
}
